from flask import Flask, render_template, request, flashapp = Flask(__name__)app.config['SECRET_KEY'] = 'bk3gi89d883jki4k'menu = [{"name": "Регистрация", "url": "reg"},        {"name": "Анализ заездов", "url": "first-app"},        {"name": "Добавить соревнование", "url": "contact"}]@app.route("/")def index():    return render_template('index.html', menu=menu)@app.route("/about")def about():    return render_template('about.html', title="О сайте", menu=menu)@app.route('/reg', methods=['POST', 'GET'])def reg():    if request.method == 'POST':        if len(request.form['usename']) > 2:            flash('Успешная регистрация', category='success')        else:            flash('Этот аккаунт уже зарегистрирован', category='error')    return render_template('contact.html', title='Регистрация', menu=menu)@app.route("/contact", methods=["POST", "GET"])def contact():    if request.method == 'POST':        if len(request.form['name of competition']) > 2:            flash('Сообщение отправлено', category='success')        else:            flash('Ошибка отправки', category='error')    return render_template('contact.html', title="Обратная связь", menu=menu)@app.errorhandler(404)def pageNotFound(error):    return render_template('page404.html', title="Страница не найдена", menu=menu)if __name__ == "__main__":    app.run(debug=True)#gift govno 100 %